mapper.select:

select no
from company
where user_no = #{userNo}

mapper.insert:

insert into pension(
	no
	,company_no
	,name
	,postal_code
	,address
	,detail_address
	,law_code
	,law_name
	,visit_road1
	,visit_road2
	,visit_road3
	,area_info1
	,area_info2
	,area_info3
	,rules
	)
values(
	SEQ_PENSION_NO.nextval
	,#{companyNo}
	,#{pensionName}
	,#{postalCode}
	,#{pensionAddress}
	,#{detailAddress}
	,#{lawCode}
	,#{lawName}
	,#{visitRoad1}
	,#{visitRoad2}
	,#{visitRoad3}
	,#{areaInfo1}
	,#{areaInfo2}
	,#{areaInfo3}
	,#{rules}
	)

mapper.update:

update pension
set
	live_pay = #{livePay}
    ,parking_info = #{parkingInfo}
    ,etc = #{etc}
where no = #{pensionNo}
